export function addCoursetoTerm(courseName, shortName,description, termid) {
  const courseid = "" + Date.now();
  //debugger; // eslint-disable-line no-debugger
  courses.push({ uid: courseid, courseName: courseName,description:description, shortName: shortName });
  var i = pastTerms.findIndex((term) => term.uid === termid);
  if (i === -1) {
    i = futureTerms.findIndex((term) => term.uid === termid);
    i === -1
      ? currentTerm.courses.push(courseid)
      : futureTerms[i].courses.push(courseid);
  } else {
    pastTerms[i].courses.push(courseid);
  }
   
}

export function deleteCourseFromTerm(courseid,termid){
  var i = pastTerms.findIndex((term) => term.uid === termid);
  if (i === -1) {
    i = futureTerms.findIndex((term) => term.uid === termid);
    i === -1
      ? (currentTerm.courses.splice(currentTerm.courses.findIndex((course)=>course===courseid),1))
      : (futureTerms[i].courses.splice(futureTerms[i].courses.findIndex((course)=>course===courseid),1));
  } else {
    pastTerms[i].courses.splice(pastTerms[i].courses.findIndex((course)=>course===courseid),1);
  }
}